import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from '@material-ui/core/Button';

const Characters = () => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/characters');
        setCharacters(response.data.results);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const addToFavorites = async (characterUrl) => {
    try {
      const userId = 'user123'; // Это должен быть ID текущего пользователя
      await axios.post('http://localhost:5000/api/favorites', { userId, characterUrl });
      alert('Character added to favorites!');
    } catch (error) {
      console.error('Error adding to favorites:', error);
    }
  };

  return (
    <div>
      <h1>Characters</h1>
      {characters.map((character, index) => (
        <div key={index}>
          <h3>{character.name}</h3>
          <p>Gender: {character.gender}</p>
          <Button color="primary" onClick={() => addToFavorites(character.url)}>Add to Favorites</Button>
        </div>
      ))}
    </div>
  );
};

export default Characters;
